configuration.mdsQueryLastJobs:
PREFIX silkspec:  <http://vocab.deri.ie/LinkSpec#>
PREFIX purl: <http://purl.org/net/provenance/ns#>
PREFIX dc: <http://purl.org/dc/terms/#>
SELECT ?id ?createdBy ?createdAt ?performedBy ?performedAt  WHERE {
?s  a  silkspec:SilkSpec;
  silkspec:ID ?id;
  dc:created ?created;
  purl:createdBy ?createdByO;
  purl:retrievedBy ?retrievedByO.

  ?createdByO purl:performedBy ?createdBy;
              purl:performedAt ?createdAt.

  ?retrievedByO purl:performedBy ?performedBy;
                purl:performedAt ?performedAt.
}
ORDER BY DESC (?performedBy)
LIMIT 100

configuration.mdsQueryLastUserJobs:
PREFIX silkspec:  <http://vocab.deri.ie/LinkSpec#>
PREFIX purl: <http://purl.org/net/provenance/ns#>
PREFIX dc: <http://purl.org/dc/terms/#>
SELECT ?id ?createdBy ?createdAt ?performedBy ?performedAt  WHERE {
?s  a  silkspec:SilkSpec;
  silkspec:ID ?id;
  dc:created ?created;
  purl:createdBy ?createdByO;
  purl:retrievedBy ?retrievedByO.

  ?createdByO purl:performedBy ?createdBy;
              purl:performedAt ?createdAt.

  ?retrievedByO purl:performedBy ?performedBy;
                purl:performedAt ?performedAt.
}
FILTER regex(str(?createdBy), "^.*##USERNAME##$")
ORDER BY DESC (?performedBy)
LIMIT 100
